import React, { useEffect, useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import './NominationForm.css';

const NominationForm = () => {
  const [formData, setFormData] = useState({
    employeeName: '',
    employeeId: '',
    department: '',
    designation: '',
    yearOfNomination: new Date().getFullYear() - 1,
    awardType: '',
    filledDoc: null,
    nominatorName: '',
    nominatorDept: '',
    nominatorDesig: '',
  });

  const [employees, setEmployees] = useState([]);
  const [showDoc, setShowDoc] = useState(false);
  const [docURL, setDocURL] = useState('');

  // Load Excel from remote
  useEffect(() => {
    const loadExcel = async () => {
      const fileUrl = "https://conceptiasoftware-my.sharepoint.com/personal/nandhini_m_ckonnect_in/_layouts/15/download.aspx?share=EbCwHjKATfRNmWyrskQyvs0BW4ng9WPdnqu_K3RwTLq_gA";
      const response = await fetch(fileUrl);
      const arrayBuffer = await response.arrayBuffer();
      const workbook = XLSX.read(arrayBuffer, { type: 'array' });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const data = XLSX.utils.sheet_to_json(sheet);
      setEmployees(data);
    };

    loadExcel();
  }, []);

  const handleSelectChange = (e) => {
    const { name, value } = e.target;
    const selected = employees.find(emp => emp["Employee Name"] === value);
    if (name === 'employeeName' && selected) {
      setFormData({
        ...formData,
        employeeName: value,
        employeeId: selected["Employee ID"],
        department: selected["Department"],
        designation: selected["Designation"],
      });
    } else if (name === 'nominatorName' && selected) {
      setFormData({
        ...formData,
        nominatorName: value,
        nominatorDept: selected["Department"],
        nominatorDesig: selected["Designation"],
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleAwardChange = (e) => {
    const award = e.target.value;
    let file = '';
    if (['Team Awesome Award', 'Top Performance - Honor Club'].includes(award)) {
      file = 'team_award.docx';
    } else if (['Peer Appreciation Award', 'Customer Service Performance', 'Spotlight Award'].includes(award)) {
      file = 'peer_award.docx';
    } else if (['Outstanding Leadership', 'Beyond the Call of Duty', 'Ace of Initiative'].includes(award)) {
      file = 'leadership_award.docx';
    }

    if (file) {
      setShowDoc(true);
      setDocURL(`/${file}`); // Ensure files are placed in `public/`
    } else {
      setShowDoc(false);
    }

    setFormData({ ...formData, awardType: award });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, filledDoc: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(formData).forEach(key => {
      data.append(key, formData[key]);
    });

    try {
      await axios.post('http://localhost:5000/api/nominations', data);
      alert('Form submitted successfully!');
    } catch (err) {
      console.error(err);
      alert('Submission failed.');
    }
  };

  return (
    <form className="award-form" onSubmit={handleSubmit} encType="multipart/form-data">
      <h2>Annual Nomination Form</h2>

      {/* Employee section */}
      <label>Employee Name</label>
      <select name="employeeName" required onChange={handleSelectChange}>
        <option value="">-- Select Employee --</option>
        {employees.map(emp => (
          <option key={emp["Employee ID"]} value={emp["Employee Name"]}>{emp["Employee Name"]}</option>
        ))}
      </select>

      <label>Employee ID</label>
      <input type="text" value={formData.employeeId} readOnly />

      <label>Department</label>
      <input type="text" value={formData.department} readOnly />

      <label>Designation</label>
      <input type="text" value={formData.designation} readOnly />

      <label>Year of Nomination</label>
      <input type="text" value={formData.yearOfNomination} readOnly />

      <label>Award Type</label>
      <select name="awardType" required onChange={handleAwardChange}>
        <option value="">-- Select Award Type --</option>
        <option value="Team Awesome Award">Team Awesome Award</option>
        <option value="Peer Appreciation Award">Peer Appreciation Award</option>
        <option value="Customer Service Performance">Customer Service Performance</option>
        <option value="Outstanding Leadership">Outstanding Leadership</option>
        <option value="Beyond the Call of Duty">Beyond the Call of Duty</option>
        <option value="Ace of Initiative">Ace of Initiative</option>
        <option value="Spotlight Award">Spotlight Award</option>
        <option value="Top Performance - Honor Club">Top Performance - Honor Club</option>
      </select>

      {showDoc && (
        <div>
          <p>Please download and fill the document:</p>
          <a href={docURL} download>Download {docURL.split('/').pop()}</a>
        </div>
      )}

      <label>Upload Completed Document</label>
      <input type="file" name="filledDoc" accept=".doc,.docx,.pdf" onChange={handleFileChange} required />

      {/* Nominator Section */}
      <label>Nominator Name</label>
      <select name="nominatorName" required onChange={handleSelectChange}>
        <option value="">-- Select Nominator --</option>
        {employees.map(emp => (
          <option key={emp["Employee ID"]} value={emp["Employee Name"]}>{emp["Employee Name"]}</option>
        ))}
      </select>

      <label>Nominator Department</label>
      <input type="text" value={formData.nominatorDept} readOnly />

      <label>Nominator Designation</label>
      <input type="text" value={formData.nominatorDesig} readOnly />

      <button type="submit">Submit Form</button>
    </form>
  );
};

export default NominationForm;
