import React, { useEffect, useState } from "react";
import axios from "axios";
import "./NominationForm.css";

const NominationForm = () => {
  const [data, setData] = useState([]);
  const [divisions, setDivisions] = useState([]);
  const [selectedDivision, setSelectedDivision] = useState("");

  const currentDate = new Date();
  const formattedMonthYear = `${currentDate.toLocaleString("default", {
    month: "long",
  })} ${currentDate.getFullYear()}`;

  const [docUrl, setDocUrl] = useState("");
  const [showExtras, setShowExtras] = useState(false);

  const [form, setForm] = useState({
    employeeName: "",
    employeeId: "",
    department: "",
    designation: "",
    yearOfNomination: formattedMonthYear,
    awardType: "",
    filledDoc: null,
    justification: "",
    recommendation: "",
    nominatorName: "",
    nominatorDept: "",
    nominatorDesig: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/employees");
        setData(res.data);

        const uniqueDivisions = [
          ...new Set(res.data.map((emp) => emp.division)),
        ];
        setDivisions(uniqueDivisions);
      } catch (err) {
        console.error("Failed to load employee data:", err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "division") {
      setSelectedDivision(value);
      setForm((prev) => ({
        ...prev,
        employeeName: "",
        employeeId: "",
        department: "",
        designation: "",
      }));
      return;
    }

    if (name === "awardType") {
      let url = "";
      let show = true;

      switch (value) {
        case "Team Awesome Award":
        case "Top Performance - Honor Club":
          url = "team_award.docx";
          break;

        case "Peer Appreciation Award":
        case "Customer Service Performance":
        case "Spotlight Award":
          url = "peer_award.docx";
          break;

        case "Outstanding Leadership":
        case "Beyond the Call of Duty":
        case "Ace of Initiative":
          url = "leadership_award.docx";
          break;

        default:
          show = false;
          url = "";
      }

      setDocUrl(url);
      setShowExtras(show);
    }

    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));

    if (name === "employeeName") {
      const emp = data.find((emp) => emp.name === value);
      if (emp) {
        setForm((prev) => ({
          ...prev,
          employeeId: emp.empId,
          department: emp.department,
          designation: emp.designation,
        }));
      }
    }

    if (name === "nominatorName") {
      const nom = data.find((emp) => emp.name === value);
      if (nom) {
        setForm((prev) => ({
          ...prev,
          nominatorDept: nom.department,
          nominatorDesig: nom.designation,
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const fd = new FormData();
    Object.entries(form).forEach(([key, val]) => {
      fd.append(key, val);
    });

    try {
      await axios.post("http://localhost:5000/api/nominations", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Nomination submitted successfully!");

      // Reset form
      setForm({
        employeeName: "",
        employeeId: "",
        department: "",
        designation: "",
        yearOfNomination: formattedMonthYear,
        awardType: "",
        filledDoc: null,
        justification: "",
        recommendation: "",
        nominatorName: "",
        nominatorDept: "",
        nominatorDesig: "",
      });
      setSelectedDivision("");
      setDocUrl("");
      setShowExtras(false);

      const fileInput = document.querySelector('input[name="filledDoc"]');
      if (fileInput) {
        fileInput.value = "";
      }
    } catch (err) {
      console.error("Submission failed:", err);
      alert("❌ Submission failed");
    }
  };

  const filteredEmployees = data.filter(
    (emp) => emp.division === selectedDivision
  );

  return (
    <form
      className="award-form"
      onSubmit={handleSubmit}
      encType="multipart/form-data"
    >
      <h2>Annual Award Nomination</h2>

      <label htmlFor="division">Select Division</label>
      <select
        name="division"
        value={selectedDivision}
        required
        onChange={handleChange}
      >
        <option value="">-- Select Division --</option>
        {divisions.map((div) => (
          <option key={div} value={div}>
            {div}
          </option>
        ))}
      </select>

      <label htmlFor="employeeName">Employee Name</label>
      <select
        name="employeeName"
        required
        value={form.employeeName}
        onChange={handleChange}
        disabled={!selectedDivision}
      >
        <option value="">-- Select Employee --</option>
        {filteredEmployees.map((emp) => (
          <option key={emp.empId} value={emp.name}>
            {emp.name}
          </option>
        ))}
      </select>

      <label>Employee ID</label>
      <input
        name="employeeId"
        readOnly
        value={form.employeeId}
        placeholder="Employee ID"
      />

      <label>Department</label>
      <input
        name="department"
        readOnly
        value={form.department}
        placeholder="Department"
      />

      <label>Designation</label>
      <input
        name="designation"
        readOnly
        value={form.designation}
        placeholder="Designation"
      />

      <label>Year of Nomination</label>
      <input name="yearOfNomination" readOnly value={form.yearOfNomination} />

      <label>Award Type</label>
      <select
        name="awardType"
        required
        value={form.awardType}
        onChange={handleChange}
      >
        <option value="">-- Select Award Type --</option>
        <option value="Team Awesome Award">Team Awesome Award</option>
        <option value="Peer Appreciation Award">
          Peer Appreciation Award / You make the Difference
        </option>
        <option value="Customer Service Performance">
          Customer Service Performance / Star Service Champion / Customer Hero
          Award
        </option>
        <option value="Outstanding Leadership">Outstanding Leadership</option>
        <option value="Beyond the Call of Duty">Beyond the Call of Duty</option>
        <option value="Ace of Initiative">Ace of Initiative</option>
        <option value="Spotlight Award">
          Spotlight Award / Certificate of Recommendation
        </option>
        <option value="Top Performance - Honor Club">
          Top Performance - Honor Club
        </option>
      </select>

      {/* Document Link and Extra Fields */}
      {showExtras && (
        <>
          <div style={{ marginTop: "10px" }}>
            <p>Please download and fill the document:</p>
            <a
              href={docUrl}
              target="_blank"
              rel="noopener noreferrer"
              download
            >
              Download {docUrl}
            </a>
          </div>

          <label htmlFor="justification">Justification</label>
          <textarea
            name="justification"
            required
            value={form.justification}
            onChange={handleChange}
            rows="4"
          ></textarea>

          <label htmlFor="recommendation">Recommendation</label>
          <textarea
            name="recommendation"
            required
            value={form.recommendation}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </>
      )}

      <label htmlFor="filledDoc">Upload Completed Document</label>
      <input
        type="file"
        name="filledDoc"
        accept=".doc,.docx,.pdf"
        required
        onChange={handleChange}
      />

      <label htmlFor="nominatorName">Nominator Name</label>
      <select
        name="nominatorName"
        required
        value={form.nominatorName}
        onChange={handleChange}
      >
        <option value="">-- Select Nominator --</option>
        {data.map((emp) => (
          <option key={emp.empId} value={emp.name}>
            {emp.name}
          </option>
        ))}
      </select>

      <label>Nominator Department</label>
      <input
        name="nominatorDept"
        readOnly
        value={form.nominatorDept}
        placeholder="Nominator Department"
      />

      <label>Nominator Designation</label>
      <input
        name="nominatorDesig"
        readOnly
        value={form.nominatorDesig}
        placeholder="Nominator Designation"
      />

      <button type="submit">Submit Nomination</button>
    </form>
  );
};

export default NominationForm;
