import React, { useEffect, useState } from "react";
import axios from "axios";
import "./NominationForm.css";

const questionMap = {
  "Team Awesome Award": [
    "Describe how the team collaborated to achieve success.",
    "What impact did the team's efforts have on business outcomes?",
  ],
  "Peer Appreciation Award": [
    "How has the nominee supported their peers beyond expectations?",
    "Share a specific instance of teamwork demonstrated by the nominee.",
  ],
  "Customer Service Performance": [
    "Describe a moment the nominee went above and beyond for a customer.",
    "How has the nominee impacted customer satisfaction?",
  ],
  "Outstanding Leadership": [
    "How has the nominee demonstrated exceptional leadership?",
    "What results were achieved under the nominee's leadership?",
  ],
  "Beyond the Call of Duty": [
    "Describe an incident where the nominee exceeded their responsibilities.",
    "What challenges did they overcome and how?",
  ],
  "Ace of Initiative": [
    "How did the nominee demonstrate proactive behavior?",
    "What improvements or innovations did their initiative lead to?",
  ],
  "Spotlight Award": [
    "What standout accomplishment led to this nomination?",
    "How has the nominee inspired others through their performance?",
  ],
  "Top Performance - Honor Club": [
    "What consistent top performance has the nominee shown?",
    "Share metrics or examples that demonstrate excellence.",
  ],
};

const NominationForm = () => {
  const [employees, setEmployees] = useState([]);
  const [divisions, setDivisions] = useState([]);
  const [selectedDivision, setSelectedDivision] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const currentDate = new Date();
  const formattedMonthYear = `${currentDate.toLocaleString("default", {
    month: "long",
  })} ${currentDate.getFullYear() - 1}`;

  const [docUrl, setDocUrl] = useState("");
  const [showExtras, setShowExtras] = useState(false);
  const [awardQuestions, setAwardQuestions] = useState(["", ""]);

  const [form, setForm] = useState({
    employeeName: "",
    employeeId: "",
    employeeEmail: "",
    department: "",
    designation: "",
    yearOfNomination: formattedMonthYear,
    awardType: "",
    filledDoc: null,
    justification: "",
    recommendation: "",
    nominatorName: "",
    nominatorDept: "",
    nominatorDesig: "",
    nominatorEmail: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [employeesRes, divisionsRes] = await Promise.all([
          axios.get("http://localhost:5000/api/employees"),
          axios.get("http://localhost:5000/api/employees/divisions")
        ]);
        
        setEmployees(employeesRes.data);
        setDivisions(divisionsRes.data);
        setIsLoading(false);
      } catch (err) {
        console.error("Failed to load data:", err);
        setError("Failed to load data. Please try again later.");
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "division") {
      setSelectedDivision(value);
      setForm(prev => ({
        ...prev,
        employeeName: "",
        employeeId: "",
        employeeEmail: "",
        department: "",
        designation: ""
      }));
      return;
    }

    if (name === "awardType") {
      const urlMap = {
        "Team Awesome Award": "team_award.docx",
        "Top Performance - Honor Club": "team_award.docx",
        "Peer Appreciation Award": "peer_award.docx",
        "Customer Service Performance": "peer_award.docx",
        "Spotlight Award": "peer_award.docx",
        "Outstanding Leadership": "leadership_award.docx",
        "Beyond the Call of Duty": "leadership_award.docx",
        "Ace of Initiative": "leadership_award.docx"
      };

      setDocUrl(urlMap[value] || "");
      setShowExtras(!!urlMap[value]);
      setAwardQuestions(questionMap[value] || ["", ""]);
    }

    setForm(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));

    if (name === "employeeName") {
      const employee = employees.find(emp => emp.name === value);
      if (employee) {
        setForm(prev => ({
          ...prev,
          employeeId: employee.empId,
          department: employee.department,
          designation: employee.designation,
          employeeEmail: employee.email
        }));
      }
    }

    if (name === "nominatorName") {
      const nominator = employees.find(emp => emp.name === value);
      if (nominator) {
        setForm(prev => ({
          ...prev,
          nominatorDept: nominator.department,
          nominatorDesig: nominator.designation,
          nominatorEmail: nominator.email
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!form.filledDoc) {
      alert("Please upload the completed document");
      return;
    }

    try {
      const formData = new FormData();
      Object.entries(form).forEach(([key, value]) => {
        if (value !== null && value !== undefined) {
          formData.append(key, value);
        }
      });

      // Add structured answers
      const answers = awardQuestions.map((question, index) => ({
        question,
        answer: index === 0 ? form.justification : form.recommendation
      }));
      formData.append("answers", JSON.stringify(answers));

      await axios.post("http://localhost:5000/api/nominations", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });

      alert("Nomination submitted successfully!");
      resetForm();
    } catch (err) {
      console.error("Submission failed:", err);
      alert("Submission failed. Please try again.");
    }
  };

  const resetForm = () => {
    setForm({
      employeeName: "",
      employeeId: "",
      employeeEmail: "",
      department: "",
      designation: "",
      yearOfNomination: formattedMonthYear,
      awardType: "",
      filledDoc: null,
      justification: "",
      recommendation: "",
      nominatorName: "",
      nominatorDept: "",
      nominatorDesig: "",
      nominatorEmail: "",
    });
    setSelectedDivision("");
    setDocUrl("");
    setShowExtras(false);
    setAwardQuestions(["", ""]);
    
    const fileInput = document.querySelector('input[name="filledDoc"]');
    if (fileInput) fileInput.value = "";
  };

  const filteredEmployees = selectedDivision
    ? employees.filter(emp => emp.division === selectedDivision)
    : [];

  if (isLoading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <form className="award-form" onSubmit={handleSubmit} encType="multipart/form-data">
      <h2>Annual Award Nomination</h2>

      <div className="form-section">
        <h3>Nominee Information</h3>
        
        <div className="form-group">
          <label htmlFor="division">Division</label>
          <select
            name="division"
            value={selectedDivision}
            required
            onChange={handleChange}
          >
            <option value="">-- Select Division --</option>
            {divisions.map(division => (
              <option key={division} value={division}>
                {division}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="employeeName">Employee Name</label>
          <select
            name="employeeName"
            required
            value={form.employeeName}
            onChange={handleChange}
            disabled={!selectedDivision}
          >
            <option value="">-- Select Employee --</option>
            {filteredEmployees.map(employee => (
              <option key={employee.empId} value={employee.name}>
                {employee.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Employee ID</label>
            <input readOnly value={form.employeeId} />
          </div>
          <div className="form-group">
            <label>Employee Email</label>
            <input readOnly value={form.employeeEmail} />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Department</label>
            <input readOnly value={form.department} />
          </div>
          <div className="form-group">
            <label>Designation</label>
            <input readOnly value={form.designation} />
          </div>
        </div>
      </div>

      <div className="form-section">
        <h3>Award Information</h3>
        
        <div className="form-group">
          <label>Year of Nomination</label>
          <input readOnly value={form.yearOfNomination} />
        </div>

        <div className="form-group">
          <label htmlFor="awardType">Award Type</label>
          <select
            name="awardType"
            required
            value={form.awardType}
            onChange={handleChange}
          >
            <option value="">-- Select Award Type --</option>
            {Object.keys(questionMap).map(award => (
              <option key={award} value={award}>
                {award}
              </option>
            ))}
          </select>
        </div>

        {showExtras && (
          <>
            <div className="document-download">
              <p>Please download and fill the document:</p>
              <a href={docUrl} target="_blank" rel="noopener noreferrer" download>
                Download {docUrl}
              </a>
            </div>

            <div className="form-group">
              <label htmlFor="justification">{awardQuestions[0]}</label>
              <textarea
                name="justification"
                required
                value={form.justification}
                onChange={handleChange}
                rows="4"
              />
            </div>

            <div className="form-group">
              <label htmlFor="recommendation">{awardQuestions[1]}</label>
              <textarea
                name="recommendation"
                required
                value={form.recommendation}
                onChange={handleChange}
                rows="4"
              />
            </div>
          </>
        )}

        <div className="form-group">
          <label htmlFor="filledDoc">Upload Completed Document</label>
          <input
            type="file"
            name="filledDoc"
            accept=".doc,.docx,.pdf"
            required={showExtras}
            onChange={handleChange}
          />
        </div>
      </div>

      <div className="form-section">
        <h3>Nominator Information</h3>
        
        <div className="form-group">
          <label htmlFor="nominatorName">Nominator Name</label>
          <select
            name="nominatorName"
            required
            value={form.nominatorName}
            onChange={handleChange}
          >
            <option value="">-- Select Nominator --</option>
            {employees.map(employee => (
              <option key={employee.empId} value={employee.name}>
                {employee.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Nominator Email</label>
            <input readOnly value={form.nominatorEmail} />
          </div>
          <div className="form-group">
            <label>Nominator Department</label>
            <input readOnly value={form.nominatorDept} />
          </div>
        </div>

        <div className="form-group">
          <label>Nominator Designation</label>
          <input readOnly value={form.nominatorDesig} />
        </div>
      </div>

      <button type="submit" className="submit-button">
        Submit Nomination
      </button>
    </form>
  );
};

export default NominationForm;
