import React, { useEffect, useState } from "react";
import axios from "axios";
import "./NominationForm.css";

const questionMap = {
  "Team Awesome Award": [
    "Describe how the team collaborated to achieve success.",
    "What impact did the team's efforts have on business outcomes?",
  ],
  "Peer Appreciation Award": [
    "How has the nominee supported their peers beyond expectations?",
    "Share a specific instance of teamwork demonstrated by the nominee.",
  ],
  "Customer Service Performance": [
    "Describe a moment the nominee went above and beyond for a customer.",
    "How has the nominee impacted customer satisfaction?",
  ],
  "Outstanding Leadership": [
    "How has the nominee demonstrated exceptional leadership?",
    "What results were achieved under the nominee’s leadership?",
  ],
  "Beyond the Call of Duty": [
    "Describe an incident where the nominee exceeded their responsibilities.",
    "What challenges did they overcome and how?",
  ],
  "Ace of Initiative": [
    "How did the nominee demonstrate proactive behavior?",
    "What improvements or innovations did their initiative lead to?",
  ],
  "Spotlight Award": [
    "What standout accomplishment led to this nomination?",
    "How has the nominee inspired others through their performance?",
  ],
  "Top Performance - Honor Club": [
    "What consistent top performance has the nominee shown?",
    "Share metrics or examples that demonstrate excellence.",
  ],
};

const NominationForm = () => {
  const [data, setData] = useState([]);
  const [divisions, setDivisions] = useState([]);
  const [selectedDivision, setSelectedDivision] = useState("");

  const currentDate = new Date();
  const formattedMonthYear = `${currentDate.toLocaleString("default", {
    month: "long",
  })} ${currentDate.getFullYear() - 1}`;

  const [docUrl, setDocUrl] = useState("");
  const [showExtras, setShowExtras] = useState(false);
  const [awardQuestions, setAwardQuestions] = useState(["", ""]);

  const [form, setForm] = useState({
    employeeName: "",
    employeeId: "",
    employeeEmail: "", 
    department: "",
    designation: "",
    yearOfNomination: formattedMonthYear,
    awardType: "",
    filledDoc: null,
    justification: "",
    recommendation: "",
    nominatorName: "",
    nominatorDept: "",
    nominatorDesig: "",
    nominatorEmail: "", 
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/employees");
        setData(res.data);
        const uniqueDivisions = [
          ...new Set(res.data.map((emp) => emp.division)),
        ];
        setDivisions(uniqueDivisions);
      } catch (err) {
        console.error("Failed to load employee data:", err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "division") {
      setSelectedDivision(value);
      setForm((prev) => ({
        ...prev,
        employeeName: "",
        employeeId: "",
        employeeEmail: "",
        department: "",
        designation: "",
        nominatorEmail: "",
      }));
      return;
    }

    if (name === "awardType") {
      let url = "";
      let show = true;

      switch (value) {
        case "Team Awesome Award":
        case "Top Performance - Honor Club":
          url = "team_award.docx";
          break;

        case "Peer Appreciation Award":
        case "Customer Service Performance":
        case "Spotlight Award":
          url = "peer_award.docx";
          break;

        case "Outstanding Leadership":
        case "Beyond the Call of Duty":
        case "Ace of Initiative":
          url = "leadership_award.docx";
          break;

        default:
          show = false;
          url = "";
      }

      setDocUrl(url);
      setShowExtras(show);
      setAwardQuestions(questionMap[value] || ["", ""]);
    }

    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));

    if (name === "employeeName") {
      const emp = data.find((emp) => emp.name === value);
      if (emp) {
        setForm((prev) => ({
          ...prev,
          employeeId: emp.empId,
          department: emp.department,
          designation: emp.designation,
          employeeEmail: emp.email,
        }));
      }
    }

    if (name === "nominatorName") {
      const nom = data.find((emp) => emp.name === value);
      if (nom) {
        setForm((prev) => ({
          ...prev,
          nominatorDept: nom.department,
          nominatorDesig: nom.designation,
          nominatorEmail: nom.email,
        }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const employee = data.find((emp) => emp.name === form.employeeName);
      const nominator = data.find((emp) => emp.name === form.nominatorName);

      if (!employee || !nominator) {
        alert("Invalid employee or nominator selected.");
        return;
      }

      const fd = new FormData();
      fd.append("employeeName", form.employeeName);
      fd.append("employeeId", form.employeeId);
      fd.append("department", form.department);
      fd.append("designation", form.designation);
      fd.append("employeeEmail", form.employeeEmail); // ✅ new
      fd.append("yearOfNomination", form.yearOfNomination);
      fd.append("awardType", form.awardType);
      fd.append("filledDoc", form.filledDoc);
      fd.append("justification", form.justification);
      fd.append("recommendation", form.recommendation);
      fd.append("nominatorName", form.nominatorName);
      fd.append("nominatorDept", form.nominatorDept);
      fd.append("nominatorDesig", form.nominatorDesig);
      fd.append("nominatorEmail", form.nominatorEmail); // ✅ new

      // Add structured answers
      const answers = [
        { question: awardQuestions[0], answer: form.justification },
        { question: awardQuestions[1], answer: form.recommendation },
      ];
      fd.append("answers", JSON.stringify(answers));

      await axios.post("http://localhost:5000/api/nominations", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Nomination submitted successfully!");

      // Reset form
      setForm({
        employeeName: "",
        employeeId: "",
        employeeEmail: "",
        department: "",
        designation: "",
        yearOfNomination: formattedMonthYear,
        awardType: "",
        filledDoc: null,
        justification: "",
        recommendation: "",
        nominatorName: "",
        nominatorDept: "",
        nominatorDesig: "",
        nominatorEmail: "",
      });
      setSelectedDivision("");
      setDocUrl("");
      setShowExtras(false);
      setAwardQuestions(["", ""]);

      const fileInput = document.querySelector('input[name="filledDoc"]');
      if (fileInput) fileInput.value = "";
    } catch (err) {
      console.error("❌ Submission failed:", err);
      alert("❌ Submission failed");
    }
  };


  const filteredEmployees = data.filter(
    (emp) => emp.division === selectedDivision
  );

  return (
    <form
      className="award-form"
      onSubmit={handleSubmit}
      encType="multipart/form-data"
    >
      <h2>Annual Award Nomination</h2>

      <label htmlFor="division">Select Division</label>
      <select
        name="division"
        value={selectedDivision}
        required
        onChange={handleChange}
      >
        <option value="">-- Select Division --</option>
        {divisions.map((div) => (
          <option key={div} value={div}>
            {div}
          </option>
        ))}
      </select>

      <label htmlFor="employeeName">Employee Name</label>
      <select
        name="employeeName"
        required
        value={form.employeeName}
        onChange={handleChange}
        disabled={!selectedDivision}
      >
        <option value="">-- Select Employee --</option>
        {filteredEmployees.map((emp) => (
          <option key={emp.empId} value={emp.name}>
            {emp.name}
          </option>
        ))}
      </select>

      <label>Employee ID</label>
      <input name="employeeId" readOnly value={form.employeeId} />

      <label>Employee Email</label>
     <input name="employeeEmail" readOnly value={form.employeeEmail || ""} />

      <label>Department</label>
      <input name="department" readOnly value={form.department || ""} />

      <label>Designation</label>
      <input name="designation" readOnly value={form.designation} />

      <label>Year of Nomination</label>
      <input name="yearOfNomination" readOnly value={form.yearOfNomination} />

      <label>Award Type</label>
      <select
        name="awardType"
        required
        value={form.awardType}
        onChange={handleChange}
      >
        <option value="">-- Select Award Type --</option>
        {Object.keys(questionMap).map((award) => (
          <option key={award} value={award}>
            {award}
          </option>
        ))}
      </select>

      {showExtras && (
        <>
          <div style={{ marginTop: "10px" }}>
            <p>Please download and fill the document:</p>
            <a
              href={docUrl}
              target="_blank"
              rel="noopener noreferrer"
              download
            >
              Download {docUrl}
            </a>
          </div>

          {/* Custom questions */}
          <label htmlFor="justification">{awardQuestions[0]}</label>
          <textarea
            name="justification"
            required
            value={form.justification}
            onChange={handleChange}
            rows="4"
          ></textarea>

          <label htmlFor="recommendation">{awardQuestions[1]}</label>
          <textarea
            name="recommendation"
            required
            value={form.recommendation}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </>
      )}

      <label htmlFor="filledDoc">Upload Completed Document</label>
      <input
        type="file"
        name="filledDoc"
        accept=".doc,.docx,.pdf"
        required
        onChange={handleChange}
      />

      <label htmlFor="nominatorName">Nominator Name</label>
      <select
        name="nominatorName"
        required
        value={form.nominatorName}
        onChange={handleChange}
      >
        <option value="">-- Select Nominator --</option>
        {data.map((emp) => (
          <option key={emp.empId} value={emp.name}>
            {emp.name}
          </option>
        ))}
      </select>

    <label>Nominator Email</label>
    <input name="nominatorEmail" readOnly value={form.nominatorEmail || ""} />


      <label>Nominator Department</label>
      <input name="nominatorDept" readOnly value={form.nominatorDept || ""} />

      <label>Nominator Designation</label>
      <input name="nominatorDesig" readOnly value={form.nominatorDesig || ""} />

      <button type="submit">Submit Nomination</button>
    </form>
  );
};

export default NominationForm;
