import React, { useEffect, useState } from "react";
import axios from "axios";
import "./NominationForm.css";

const NominationForm = () => {
  const [data, setData] = useState([]);
  const [showDocSection, setShowDocSection] = useState(false);
  const [showExtraFields, setShowExtraFields] = useState(false);
  const [docUrl, setDocUrl] = useState("");

  const currentYear = new Date().getFullYear() - 1;

  const [form, setForm] = useState({
    employeeName: "",
    employeeId: "",
    department: "",
    designation: "",
    yearOfNomination: currentYear,
    awardType: "",
    justification: "",
    recommendation: "",
    filledDoc: null,
    nominatorName: "",
    nominatorDept: "",
    nominatorDesig: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/employees");
        setData(res.data);
      } catch (err) {
        console.error("Failed to load employee data:", err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    setForm((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));

    if (name === "employeeName") {
      const emp = data.find((emp) => emp.name === value);
      if (emp) {
        setForm((prev) => ({
          ...prev,
          employeeId: emp.empId,
          department: emp.department,
          designation: emp.designation,
        }));
      }
    }

    if (name === "nominatorName") {
      const nom = data.find((emp) => emp.name === value);
      if (nom) {
        setForm((prev) => ({
          ...prev,
          nominatorDept: nom.department,
          nominatorDesig: nom.designation,
        }));
      }
    }

    if (name === "awardType") {
      showAwardExtras(value);
    }
  };

  const showAwardExtras = (awardType) => {
    let url = "";
    switch (awardType) {
      case "Team Awesome Award":
      case "Top Performance - Honor Club":
        url = "team_award.docx";
        break;
      case "Peer Appreciation Award":
      case "Customer Service Performance":
      case "Spotlight Award":
        url = "peer_award.docx";
        break;
      case "Outstanding Leadership":
      case "Beyond the Call of Duty":
      case "Ace of Initiative":
        url = "leadership_award.docx";
        break;
      default:
        setShowDocSection(false);
        setShowExtraFields(false);
        return;
    }

    setDocUrl(url);
    setShowDocSection(true);
    setShowExtraFields(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const fd = new FormData();
    Object.entries(form).forEach(([key, val]) => {
      fd.append(key, val);
    });

    try {
      await axios.post("http://localhost:5000/api/nominations", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("✅ Nomination submitted successfully!");

      // Reset form
      setForm({
        employeeName: "",
        employeeId: "",
        department: "",
        designation: "",
        yearOfNomination: currentYear,
        awardType: "",
        justification: "",
        recommendation: "",
        filledDoc: null,
        nominatorName: "",
        nominatorDept: "",
        nominatorDesig: "",
      });
      setShowDocSection(false);
      setShowExtraFields(false);

      const fileInput = document.querySelector('input[name="filledDoc"]');
      if (fileInput) {
        fileInput.value = "";
      }
    } catch (err) {
      console.error("Submission failed:", err);
      alert("❌ Submission failed");
    }
  };

  return (
    <form className="award-form" onSubmit={handleSubmit} encType="multipart/form-data">
      <h2>Annual Nomination Form</h2>

      <div className="section-title">Nominee Details</div>

      <label htmlFor="employeeName">Employee Name</label>
      <select
        id="employeeName"
        name="employeeName"
        value={form.employeeName}
        onChange={handleChange}
        required
      >
        <option value="">-- Select Employee --</option>
        {data.map((emp) => (
          <option key={emp.empId} value={emp.name}>
            {emp.name}
          </option>
        ))}
      </select>

      <label htmlFor="employeeId">Employee ID</label>
      <input
        type="text"
        id="employeeId"
        name="employeeId"
        value={form.employeeId}
        readOnly
      />

      <label htmlFor="department">Department</label>
      <input
        type="text"
        id="department"
        name="department"
        value={form.department}
        readOnly
      />

      <label htmlFor="designation">Designation</label>
      <input
        type="text"
        id="designation"
        name="designation"
        value={form.designation}
        readOnly
      />

      <label htmlFor="yearOfNomination">Year of Nomination</label>
      <input
        type="text"
        id="yearOfNomination"
        name="yearOfNomination"
        value={form.yearOfNomination}
        readOnly
      />

      <label htmlFor="awardType">Award Type</label>
      <select
        id="awardType"
        name="awardType"
        value={form.awardType}
        onChange={handleChange}
        required
      >
        <option value="">-- Select Award Type --</option>
        <option value="Team Awesome Award">Team Awesome Award</option>
        <option value="Peer Appreciation Award">
          Peer Appreciation Award / You make the Difference
        </option>
        <option value="Customer Service Performance">
          Customer Service Performance / Star Service Champion / Customer Hero Award
        </option>
        <option value="Outstanding Leadership">Outstanding Leadership</option>
        <option value="Beyond the Call of Duty">Beyond the Call of Duty</option>
        <option value="Ace of Initiative">Ace of Initiative</option>
        <option value="Spotlight Award">
          Spotlight Award / Certificate of Recommendation
        </option>
        <option value="Top Performance - Honor Club">
          Top Performance - Honor Club
        </option>
      </select>

      {showDocSection && (
        <div id="docSection" style={{ marginTop: "10px" }}>
          <p>Please download and fill the document:</p>
          <a id="docLink" href={docUrl} target="_blank" rel="noopener noreferrer" download>
            Download {docUrl}
          </a>
        </div>
      )}

      {showExtraFields && (
        <div id="extraFields">
          <label htmlFor="justification">Justification</label>
          <textarea
            id="justification"
            name="justification"
            rows="4"
            value={form.justification}
            onChange={handleChange}
            required
          />

          <label htmlFor="recommendation">Recommendation</label>
          <textarea
            id="recommendation"
            name="recommendation"
            rows="4"
            value={form.recommendation}
            onChange={handleChange}
            required
          />
        </div>
      )}

      <label htmlFor="filledDoc">Upload Completed Document</label>
      <input
        type="file"
        id="filledDoc"
        name="filledDoc"
        accept=".doc,.docx,.pdf"
        onChange={handleChange}
        required
      />

      <div className="section-title">Nominator Details</div>

      <label htmlFor="nominatorName">Nominator Name</label>
      <select
        id="nominatorName"
        name="nominatorName"
        value={form.nominatorName}
        onChange={handleChange}
        required
      >
        <option value="">-- Select Nominator --</option>
        {data.map((emp) => (
          <option key={emp.empId} value={emp.name}>
            {emp.name}
          </option>
        ))}
      </select>

      <label htmlFor="nominatorDept">Nominator Department</label>
      <input
        type="text"
        id="nominatorDept"
        name="nominatorDept"
        value={form.nominatorDept}
        readOnly
      />

      <label htmlFor="nominatorDesig">Nominator Designation</label>
      <input
        type="text"
        id="nominatorDesig"
        name="nominatorDesig"
        value={form.nominatorDesig}
        readOnly
      />

      <button type="submit">Submit Form</button>
    </form>
  );
};

export default NominationForm;
